sys_script_include.script:
var DominosMenuAPI = Class.create();

DominosMenuAPI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

  /**
   * Returns the complete Domino's menu with items, sizes, and codes
   */
  getMenu: function() {
    var menu = {
