          sizes: [
            { size: "S", name: "Small", price: 12.90 },
            { size: "M", name: "Medium", price: 15.50 },
            { size: "L", name: "Large", price: 18.50 }
          ]
        },
        {
          item_code: "HAW",
          item_name: "Hawaiian",
          description: "Ham and pineapple with mozzarella cheese",
          sizes: [
            { size: "S", name: "Small", price: 13.90 },
            { size: "M", name: "Medium", price: 16.50 },
            { size: "L", name: "Large", price: 19.50 }
          ]
        },
        {
          item_code: "VEG",
          item_name: "Veggie",
          description: "Bell peppers, mushrooms, onions, olives, and tomatoes",
          sizes: [
            { size: "S", name: "Small", price: 12.90 },
            { size: "M", name: "Medium", price: 15.50 },
            { size: "L", name: "Large", price: 18.50 }
          ]
        }
      ]
    };

    return JSON.stringify(menu);
  },

  /**
   * Returns menu item by item code
   */
  getMenuItem: function() {
    var itemCode = this.getParameter('sysparm_item_code');
    var menu = JSON.parse(this.getMenu());

    for (var i = 0; i < menu.pizzas.length; i++) {
      if (menu.pizzas[i].item_code === itemCode) {
        return JSON.stringify(menu.pizzas[i]);
      }
    }

    return JSON.stringify({ error: 'Item not found' });
  },

  /**
   * Returns all available sizes for a specific item
   */
  getItemSizes: function() {
    var itemCode = this.getParameter('sysparm_item_code');
    var menuItem = JSON.parse(this.getMenuItem());

    if (menuItem.error) {
      return JSON.stringify(menuItem);
    }

    return JSON.stringify(menuItem.sizes);
  },

